    new Lolcat("Kitty"), 
    new Lolcat("Furry Kitty"), 
    new Trollcat("Ball of Fur")
];

var mood = Mood.Nize;

for(int i = 0; i < 30; i++)
{
    int cat = random.Int(3);
    mood = cats[cat].Say(mood);
}

