concurrent class reindeer
{
    santa _santa;
    string _name;                    

    [Forever]    
    void main(santa s, string name)
    {
        _santa = s;
        _name = name;

        {vacation()}
    }

    public void unharness()
    {
        console.write(_name + ": job well done");
    }

    private void vacation()
    {
        await wait(sec: rand(3, 7))
                -> console.write(_name + ": back from vacation")
                -> (_santa.reindeer_back(this) & unharness);
    }
}

concurrent class elf
{
    santa _santa;
    string _name;                    

    [Forever]    
    void main(santa s, string name)
    {
        _santa = s;
        _name = name;

        {work()}
    }

    public void advice(bool given)
    {
        if (given)
            console.write(_name + ": great advice, santa!");
        else 
            console.write(_name + ": too bad santa had to go");
    }

    private void work()
    {
        try
        {
            await wait(sec: rand(1, 5))
                    -> console.write(_name + ": Off to see Santa")
                    -> (_santa.elf_request(this) & advice);
        }
        catch(InvalidOperationException e)
        {
            console.write(_name + ": Santa's busy, back to work! ");
        }
    }
}

concurrent class santa
{
    List<reindeer> _reindeer = new List<reindeer>();
    List<elf>      _elves = new List<elf>();

    private bool isDelivering { get {return _reindeer.Count == 8;} }

    public void reindeer_back(reindeer r)
    {
        _reindeer.Add(r);
        if (isDelivering)
        {
            await cancel_elves();

            console.write("Santa: Off to deliver toys!");
            await wait(sec:rand(5, 10));
            console.write("Santa: Merry Christmas, enjoy the toys!");

            foreach(var rd in _reindeer)
                rd.unharness();

            _reindeer.Clear();
        }
    }

    public void elf_request(elf e)
    {
        if (isDelivering)
            await reindeer_back;

        _elves.Add(e);
        if (_elves.Count == 3)
        {
            console.write("Santa: hey guys, need help?");
            await wait(sec: rand(1, 2)) | cancel_elves;

            if (isDelivering)
                console.write("Santa: sorry fellows, got toys to deliver!");
            else
                console.write("Santa: Good to see you, little fellas!");

            foreach(var el in _elves)
                el.advice(!isDelivering);

            _elves.Clear();
        }    
    }

    private void cancel_elves();
}

var santa = spawn<santa>();
var shop = spawn<shop>();

start(santa);
start(shop, santa);

concurrent class santa
{
    bool _delivering = false;
    bool _helping = false;

    public void reindeer()
    {
        _delivering = true;
        if (!_helping)
            await deliver();    
    }

    public void elves()
    {
        if (_delivering)
        {
            console.write("elves: nobody home, back to work!");
            return;
        }

        console.write("santa: hey guys, need help?");

        _helping = true;
        await wait(sec: rand(1, 2)) | reindeer;

        _helping = false;
        if (_delivering)
        {
            console.write("santa: sorry fellows, got toys to deliver!");
            await deliver();
        }
        else
            console.write("elves: great advice santa!");
    }

    private void deliver()
    {
        console.write("santa: Off to deliver toys!");
        {wait(sec: rand(5, 7))}
        console.write("santa: Merry Christmas, enjoy the toys!");

        _delivering = false;
    }
}

concurrent class shop
{
    santa _santa;
    int   _reindeer = 0;
    int   _elves    = 0;
    void main(santa s)
    {
        _santa = s;

        {reindeer() & elves()}
    }

    [Forever]
    public void reindeer()
    {
        console.write("reindeer: back from vacation");
        _reindeer++;

        if (_reindeer == 8)
        {
            await _santa.reindeer();
            _reindeer = 0;
        }

        {wait(sec: rand(2, 3))}
    }

    [Forever]
    public void elves()
    {
        console.write("elf: need santa");
        _elves++;
        if (_elves == 3)
        {
            await _santa.elves();
            _elves = 0;
        }

        {wait(sec: rand(1, 2))}
    }
}

concurrent class philosopher 
{
    [Forever]
    void main(string name, chopstick left, chopstick right) 
    {
        _name  = name;
        _left  = left;
        _right = right;

        {hungry()}
    }

    void hungry()
    {
        console.write(_name + " is hungry");

        try
        {
            await ((_left.acquire(this) & _right.acquire(this))
                  | timeout(sec: 2.1))
                      -> eat();
        }
        catch
        {
            console.write(_name + " livelock");
            _left.release(this);
            _right.release(this);
        }
    }

    void eat()
    {
        console.write(_name + " is eating");

        {wait(sec: 1.0)
            -> (_left.release(this) & _right.release(this))}

        console.write(_name + " is full");
    }

    private string _name;
    private chopstick _left;
    private chopstick _right;
}
